import "core-js/modules/es.array.push.js";
import { notify } from '@/services/ChatService';
import { subscribe } from '@/utils/websocket';
//import { getMyAuthority } from '@/utils/security.util';

export default {
  name: 'Home',
  data() {
    return {
      //myAuthority: getMyAuthority(),
      myAuthority: 'User',
      myName: '',
      messages: [],
      newMessage: '',
      isConnected: false
    };
  },
  methods: {
    connect() {
      subscribe('/message.new', message => {
        this.messages.push(message);
      });
      this.isConnected = true;
    },
    sendNewMessage() {
      console.log('Todas as Mensagens:', this.messages);
      notify({
        name: this.myName,
        authority: this.myAuthority,
        message: this.newMessage
      }).then(() => {
        console.log('retorno?', this.newMessage);
        this.messages.push(this.newMessage);
        this.newMessage = '';
      });
    }
  }
};
