import http from '@/utils/http';

const baseUrl = 'http://localhost:9090/backend/comprador';

export function filtrarProdutos({ pagina, ordenacao, tamanho }) {
    return http({
      url: `${baseUrl}/filtrar-produtos?page=${pagina - 1}&sort=${ordenacao}&size=${tamanho}`,
      method: 'get'
  });
}

export function getVisualizacaoProduto(idProduto) {
    return http({
      url: `${baseUrl}/visualizar-produto?idProduto=${idProduto}`,
      method: 'get'
  });
}

export function enviarEmailCompra(usuarioVendedorId, produtoId){
    return http({
        url: baseUrl + '/imagem-produto?produtoId=' + produtoId,
        data: imagem,
        method: 'post'
    });
}

export function enviarImagem(imagem, produtoId){
    return http({
        url: baseUrl + '/imagem-produto?produtoId=' + produtoId,
        data: imagem,
        method: 'post'
    });
}
